import 'server-only';

/**
 * Print-optimized CSS styles for PDF generation.
 *
 * These styles are embedded directly in the HTML output and are designed
 * for Puppeteer/Chromium PDF rendering. Uses Suisse Intl for headings
 * and Suisse Intl for body text, matching the Sparlo brand.
 */

// Logo as base64 for reliable embedding in PDF
export const LOGO_BASE64 =
  'data:image/png;base64,';

// Suisse Intl fonts as base64 for reliable embedding in PDF
const SUISSE_REGULAR_BASE64 =
  '';

const SUISSE_MEDIUM_BASE64 =
  '';

const SUISSE_SEMIBOLD_BASE64 =
  '';

export const PRINT_STYLES = `
/* ============================================
   EMBEDDED FONTS (Suisse Intl)
   ============================================ */

@font-face {
  font-family: 'Suisse Intl';
  src: url(data:font/woff2;base64,${SUISSE_REGULAR_BASE64}) format('woff2');
  font-weight: 400;
  font-style: normal;
  font-display: block;
}

@font-face {
  font-family: 'Suisse Intl';
  src: url(data:font/woff2;base64,${SUISSE_MEDIUM_BASE64}) format('woff2');
  font-weight: 500;
  font-style: normal;
  font-display: block;
}

@font-face {
  font-family: 'Suisse Intl';
  src: url(data:font/woff2;base64,${SUISSE_SEMIBOLD_BASE64}) format('woff2');
  font-weight: 600;
  font-style: normal;
  font-display: block;
}

/* ============================================
   BASE RESET & PAGE SETUP
   ============================================ */

@page {
  size: A4;
  margin: 48px 48px 64px 48px;
}

@page :first {
  margin-top: 48px;
}

* {
  margin: 0;
  padding: 0;
  box-sizing: border-box;
  -webkit-print-color-adjust: exact !important;
  print-color-adjust: exact !important;
}

/* ============================================
   TYPOGRAPHY
   Suisse Intl embedded via base64 @font-face
   ============================================ */

body {
  font-family: 'Suisse Intl', 'SF Pro Display', -apple-system, BlinkMacSystemFont, 'Segoe UI', system-ui, sans-serif;
  font-size: 10pt;
  line-height: 1.5;
  color: #3f3f46; /* zinc-700 */
  background: white;
}

/* Heading font */
h1, h2, h3, h4, .report-title, .section-title, .subsection-title,
.recommendation-title, .innovation-title, .concept-title, .investigation-title {
  font-family: 'Suisse Intl', 'SF Pro Display', -apple-system, BlinkMacSystemFont, system-ui, sans-serif;
}

/* Labels - use Suisse Intl with uppercase and tracking (matches site design) */
.mono-label, .mono-label-strong, .mono-label-muted {
  font-family: 'Suisse Intl', 'SF Pro Display', -apple-system, BlinkMacSystemFont, system-ui, sans-serif;
  text-transform: uppercase;
  letter-spacing: 0.06em;
}

/* Monospace only for actual code */
code {
  font-family: 'SF Mono', ui-monospace, monospace;
}

/* ============================================
   LOGO
   ============================================ */

.report-logo {
  margin-bottom: 32px;
}

.report-logo img {
  height: 28px;
  width: auto;
}

/* ============================================
   COLORS (Zinc Palette)
   ============================================ */

:root {
  --zinc-50: #fafafa;
  --zinc-100: #f4f4f5;
  --zinc-200: #e4e4e7;
  --zinc-300: #d4d4d8;
  --zinc-400: #a1a1aa;
  --zinc-500: #71717a;
  --zinc-600: #52525b;
  --zinc-700: #3f3f46;
  --zinc-800: #27272a;
  --zinc-900: #18181b;
  --zinc-950: #09090b;
}

/* ============================================
   LAYOUT
   ============================================ */

.report-container {
  max-width: 100%;
  padding: 0;
}

/* ============================================
   HEADER
   ============================================ */

.report-header {
  margin-bottom: 56px;
  page-break-after: avoid;
}

.report-title {
  font-size: 30pt;
  font-weight: 400;
  letter-spacing: -0.02em;
  line-height: 1;
  color: var(--zinc-900);
  margin-bottom: 16px;
}

.report-meta {
  display: flex;
  align-items: center;
  gap: 10px;
  font-size: 10pt;
  color: var(--zinc-500);
}

.meta-separator {
  color: var(--zinc-300);
}

/* ============================================
   SECTIONS
   ============================================ */

.section {
  margin-top: 48px;
  page-break-inside: avoid;
}

.section-title {
  font-size: 27pt;
  font-weight: 600;
  letter-spacing: -0.02em;
  color: var(--zinc-900);
  margin-bottom: 20px;
  page-break-after: avoid;
}

.section-title-lg {
  font-size: 21pt;
  font-weight: 600;
  letter-spacing: -0.02em;
  color: var(--zinc-900);
  margin-bottom: 16px;
  page-break-after: avoid;
}

.section-title-md {
  font-size: 18pt;
  font-weight: 600;
  letter-spacing: -0.02em;
  color: var(--zinc-900);
  margin-bottom: 12px;
  page-break-after: avoid;
}

.subsection-title {
  font-size: 16pt;
  font-weight: 600;
  letter-spacing: -0.01em;
  color: var(--zinc-900);
  margin-bottom: 12px;
  margin-top: 24px;
  page-break-after: avoid;
}

.section-intro {
  font-size: 13pt;
  line-height: 1.4;
  color: var(--zinc-600);
  margin-bottom: 24px;
  max-width: 60ch;
}

/* ============================================
   CONTENT BLOCKS
   ============================================ */

.content-block {
  margin-top: 16px;
  page-break-inside: avoid;
}

.content-block + .content-block {
  margin-top: 20px;
  padding-top: 16px;
  border-top: 1px solid var(--zinc-200);
}

/* ============================================
   TYPOGRAPHY COMPONENTS
   ============================================ */

.mono-label {
  display: block;
  font-size: 10pt;
  font-weight: 600;
  color: var(--zinc-500);
  margin-bottom: 8px;
}

.mono-label-strong {
  display: block;
  font-size: 10pt;
  font-weight: 600;
  color: var(--zinc-900);
  margin-bottom: 8px;
}

.mono-label-muted {
  display: block;
  font-size: 10pt;
  font-weight: 600;
  color: var(--zinc-400);
  margin-bottom: 8px;
}

/* Primary body text - matches web's 18px */
.body-text {
  font-size: 13pt;
  line-height: 1.3;
  letter-spacing: -0.02em;
  color: #1e1e1e;
  max-width: 60ch;
}

/* Large body text - matches web's 22px */
.body-text-lg {
  font-size: 16pt;
  line-height: 1.3;
  letter-spacing: -0.02em;
  color: #1e1e1e;
  max-width: 60ch;
}

/* Secondary body text - matches web's 16px */
.body-text-secondary {
  font-size: 12pt;
  line-height: 1.4;
  color: var(--zinc-600);
  max-width: 60ch;
}

/* Lead/intro text */
.lead-text {
  font-size: 14pt;
  line-height: 1.4;
  letter-spacing: -0.01em;
  color: var(--zinc-800);
  margin-bottom: 24px;
  max-width: 60ch;
}

/* ============================================
   BOXES & HIGHLIGHTS
   ============================================ */

/* Brief box - elegant left border */
.brief-box {
  border-left: 2px solid var(--zinc-900);
  padding-left: 24px;
  padding-top: 8px;
  padding-bottom: 8px;
}

/* Insight box - heavy left border accent */
.insight-box {
  border-left: 3px solid var(--zinc-900);
  padding-left: 20px;
  margin-top: 20px;
  page-break-inside: avoid;
}

.insight-headline {
  font-size: 15pt;
  font-weight: 500;
  letter-spacing: -0.01em;
  color: var(--zinc-900);
  margin-bottom: 10px;
  line-height: 1.3;
}

.insight-block {
  border-left: 3px solid var(--zinc-900);
  padding-left: 20px;
  margin-top: 24px;
  page-break-inside: avoid;
}

.insight-what {
  font-size: 14pt;
  font-weight: 500;
  letter-spacing: -0.01em;
  color: var(--zinc-900);
  margin-bottom: 10px;
  line-height: 1.35;
}

.insight-source {
  margin-top: 12px;
  font-size: 11pt;
  color: var(--zinc-600);
  line-height: 1.4;
}

.insight-source p {
  margin-bottom: 6px;
}

.insight-source .label {
  font-weight: 500;
  color: var(--zinc-500);
}

.insight-missed {
  margin-top: 10px;
  font-size: 11pt;
  color: var(--zinc-600);
  font-style: italic;
  line-height: 1.4;
}

/* Viability box - inverted accent */
.viability-box {
  background: var(--zinc-900);
  color: white;
  padding: 20px 24px;
  margin-bottom: 24px;
  page-break-inside: avoid;
}

.viability-box .mono-label {
  color: var(--zinc-400);
}

.viability-box .body-text-lg {
  color: white;
}

.warning-box {
  background: var(--zinc-50);
  border: 1px solid var(--zinc-300);
  border-left: 3px solid var(--zinc-700);
  padding: 16px;
  margin-top: 16px;
  page-break-inside: avoid;
}

.warning-list {
  list-style: none;
  padding: 0;
  margin-top: 8px;
}

.warning-list li {
  position: relative;
  padding-left: 16px;
  margin-bottom: 6px;
  font-size: 10pt;
  color: var(--zinc-600);
}

.warning-list li::before {
  content: '';
  position: absolute;
  left: 0;
  top: 7px;
  width: 4px;
  height: 4px;
  background: var(--zinc-400);
  border-radius: 50%;
}

.breakthrough-box {
  background: var(--zinc-50);
  border: 1px solid var(--zinc-200);
  padding: 16px;
  margin-top: 16px;
  page-break-inside: avoid;
}

.confidence-box {
  background: var(--zinc-50);
  border: 1px solid var(--zinc-200);
  padding: 16px;
  margin-bottom: 20px;
  page-break-inside: avoid;
}

.confidence-value {
  font-size: 14pt;
  font-weight: 600;
  color: var(--zinc-900);
  margin-top: 4px;
}

/* ============================================
   METRICS GRID
   ============================================ */

.metrics-grid {
  display: flex;
  gap: 24px;
  background: var(--zinc-50);
  border: 1px solid var(--zinc-200);
  padding: 16px;
  margin-top: 16px;
  page-break-inside: avoid;
}

.metric-item {
  flex: 1;
}

.metric-value {
  font-size: 12pt;
  font-weight: 600;
  color: var(--zinc-900);
  margin-top: 4px;
}

.metric-highlight {
  font-size: 12pt;
  font-weight: 600;
  color: var(--zinc-900);
  margin-top: 8px;
}

/* ============================================
   LISTS
   ============================================ */

.constraint-list {
  list-style: none;
  padding: 0;
  margin-top: 12px;
}

.constraint-list li {
  position: relative;
  padding-left: 20px;
  margin-bottom: 10px;
  font-size: 12pt;
  line-height: 1.4;
  color: var(--zinc-700);
}

.constraint-list.hard li::before {
  content: '';
  position: absolute;
  left: 0;
  top: 8px;
  width: 8px;
  height: 8px;
  background: var(--zinc-900);
  border-radius: 50%;
}

.constraint-list.soft li::before {
  content: '';
  position: absolute;
  left: 0;
  top: 8px;
  width: 8px;
  height: 8px;
  background: var(--zinc-400);
  border-radius: 50%;
}

.constraint-list.assumption li::before {
  content: '';
  position: absolute;
  left: 0;
  top: 8px;
  width: 8px;
  height: 8px;
  background: var(--zinc-300);
  border-radius: 50%;
}

.numbered-list {
  list-style: none;
  padding: 0;
  margin-top: 12px;
}

.numbered-item {
  display: flex;
  gap: 12px;
  margin-bottom: 12px;
  page-break-inside: avoid;
}

.step-number {
  flex-shrink: 0;
  width: 24px;
  height: 24px;
  display: flex;
  align-items: center;
  justify-content: center;
  font-size: 9pt;
  font-weight: 600;
  color: var(--zinc-400);
}

.step-content {
  flex: 1;
}

.step-rationale {
  font-size: 9pt;
  color: var(--zinc-500);
  margin-top: 4px;
  font-style: italic;
}

.critique-list {
  list-style: none;
  padding: 0;
  margin-top: 8px;
}

.critique-list li {
  position: relative;
  padding-left: 16px;
  margin-bottom: 8px;
  font-size: 10pt;
  color: var(--zinc-600);
}

.critique-list li::before {
  content: '';
  position: absolute;
  left: 0;
  top: 6px;
  width: 6px;
  height: 6px;
  background: var(--zinc-400);
  border-radius: 50%;
}

/* ============================================
   TABLES
   ============================================ */

.benchmark-table {
  margin-top: 16px;
  overflow: hidden;
}

.benchmark-table table {
  width: 100%;
  border-collapse: collapse;
  font-size: 10pt;
}

.benchmark-table th {
  text-align: left;
  padding: 12px 16px;
  font-weight: 600;
  font-size: 10pt;
  color: var(--zinc-700);
  border-bottom: 2px solid var(--zinc-900);
}

.benchmark-table td {
  padding: 12px 16px;
  border-bottom: 1px solid var(--zinc-200);
  color: var(--zinc-600);
  font-size: 10pt;
  line-height: 1.4;
}

/* ============================================
   RECOMMENDATIONS & CONCEPTS
   ============================================ */

.primary-recommendation,
.recommended-innovation {
  margin-top: 24px;
  page-break-inside: avoid;
}

.recommendation-header,
.innovation-header {
  margin-bottom: 20px;
  page-break-after: avoid;
}

.recommendation-title,
.innovation-title {
  font-size: 18pt;
  font-weight: 600;
  letter-spacing: -0.02em;
  color: var(--zinc-900);
  margin: 10px 0;
}

.recommendation-meta,
.innovation-meta {
  display: flex;
  align-items: center;
  gap: 10px;
  font-size: 10pt;
  color: var(--zinc-500);
}

.confidence {
  font-weight: 500;
  color: var(--zinc-700);
}

.confidence-badge {
  font-size: 9pt;
  font-weight: 500;
  color: var(--zinc-700);
  background: var(--zinc-100);
  padding: 2px 8px;
  border-radius: 2px;
}

.supporting-concepts,
.parallel-investigations {
  margin-top: 24px;
}

.concepts-list,
.investigations-list {
  margin-top: 16px;
}

.concept-item,
.investigation-item {
  margin-bottom: 20px;
  padding-bottom: 16px;
  border-bottom: 1px solid var(--zinc-200);
  page-break-inside: avoid;
}

.concept-item:last-child,
.investigation-item:last-child {
  border-bottom: none;
}

.concept-header,
.investigation-header {
  display: flex;
  justify-content: space-between;
  align-items: baseline;
  gap: 12px;
  margin-bottom: 8px;
}

.concept-title,
.investigation-title {
  font-size: 12pt;
  font-weight: 600;
  color: var(--zinc-900);
}

.concept-relationship {
  font-size: 8pt;
  letter-spacing: 0.04em;
  text-transform: uppercase;
  color: var(--zinc-400);
}

.concept-oneliner,
.investigation-oneliner {
  font-size: 10pt;
  color: var(--zinc-600);
  font-style: italic;
  margin-bottom: 12px;
}

.concept-detail {
  margin-top: 12px;
}

.concept-when {
  margin-top: 12px;
  border-left: 2px solid var(--zinc-200);
  padding-left: 12px;
}

.ceiling,
.uncertainty {
  font-size: 10pt;
  color: var(--zinc-600);
  margin-top: 6px;
}

/* ============================================
   VALIDATION
   ============================================ */

.validation-gates {
  margin-top: 20px;
  padding-top: 16px;
  border-top: 1px solid var(--zinc-200);
}

.gates-list {
  margin-top: 12px;
}

.gate-item {
  padding: 12px 0 12px 16px;
  border-left: 2px solid var(--zinc-300);
  margin-bottom: 12px;
  page-break-inside: avoid;
}

.gate-header {
  display: flex;
  justify-content: space-between;
  align-items: flex-start;
  gap: 12px;
}

.gate-week {
  font-size: 8pt;
  font-weight: 500;
  color: var(--zinc-400);
}

.gate-test {
  font-size: 11pt;
  font-weight: 500;
  color: var(--zinc-900);
  margin-top: 2px;
}

.gate-cost {
  font-size: 8pt;
  color: var(--zinc-500);
  flex-shrink: 0;
}

.gate-detail {
  font-size: 10pt;
  color: var(--zinc-600);
  margin-top: 6px;
}

.gate-detail .label {
  font-weight: 500;
  color: var(--zinc-500);
}

.gate-decision {
  font-size: 9pt;
  color: var(--zinc-500);
  font-style: italic;
  margin-top: 6px;
}

.validation-path {
  margin-top: 16px;
  padding: 12px;
  background: var(--zinc-50);
  border: 1px solid var(--zinc-200);
  page-break-inside: avoid;
}

.validation-meta {
  display: flex;
  gap: 16px;
  font-size: 9pt;
  color: var(--zinc-500);
  margin-top: 8px;
}

.go-no-go {
  font-size: 10pt;
  font-weight: 500;
  color: var(--zinc-700);
  margin-top: 8px;
  padding-top: 8px;
  border-top: 1px solid var(--zinc-200);
}

.validation-gaps {
  margin-top: 12px;
}

.gap-item {
  padding: 12px;
  margin-bottom: 8px;
  background: var(--zinc-50);
  border: 1px solid var(--zinc-200);
  page-break-inside: avoid;
}

.gap-item.addressed {
  border-left: 3px solid #22c55e;
}

.gap-item.extended-needed {
  border-left: 3px solid #eab308;
}

.gap-item.accepted-risk {
  border-left: 3px solid var(--zinc-400);
}

.gap-header {
  margin-bottom: 6px;
}

.gap-status {
  font-size: 8pt;
  font-weight: 600;
  text-transform: uppercase;
  letter-spacing: 0.04em;
  color: var(--zinc-500);
}

.gap-concern {
  font-size: 10pt;
  font-weight: 500;
  color: var(--zinc-800);
  margin-bottom: 4px;
}

.gap-rationale {
  font-size: 9pt;
  color: var(--zinc-600);
}

/* ============================================
   CHALLENGE FRAME
   ============================================ */

.challenge-list {
  margin-top: 16px;
}

.challenge-item {
  margin-bottom: 20px;
  padding-bottom: 16px;
  border-bottom: 1px solid var(--zinc-200);
  page-break-inside: avoid;
}

.challenge-item:last-child {
  border-bottom: none;
}

.challenge-assumption,
.challenge-challenge {
  margin-bottom: 12px;
}

.challenge-implication {
  border-left: 2px solid var(--zinc-200);
  padding-left: 12px;
}

/* ============================================
   RISKS
   ============================================ */

.risks-list {
  margin-top: 20px;
}

.risk-item {
  padding: 16px 20px;
  margin-bottom: 16px;
  border: 1px solid var(--zinc-200);
  page-break-inside: avoid;
}

.risk-item.high {
  border-left: 3px solid var(--zinc-800);
}

.risk-item.medium {
  border-left: 3px solid var(--zinc-500);
}

.risk-item.low {
  border-left: 3px solid var(--zinc-300);
}

.risk-header {
  display: flex;
  justify-content: space-between;
  align-items: center;
  margin-bottom: 10px;
}

.risk-category {
  font-size: 10pt;
  font-weight: 600;
  text-transform: uppercase;
  letter-spacing: 0.04em;
  color: var(--zinc-500);
}

.risk-severity {
  font-size: 10pt;
  color: var(--zinc-500);
}

.risk-severity.high {
  color: var(--zinc-800);
  font-weight: 500;
}

.risk-description {
  font-size: 12pt;
  line-height: 1.4;
  color: var(--zinc-700);
  margin-bottom: 10px;
}

.risk-mitigation {
  font-size: 11pt;
  line-height: 1.4;
  color: var(--zinc-600);
}

.risk-section {
  margin-top: 16px;
}

.risk-label {
  font-size: 10pt;
  font-weight: 500;
  color: var(--zinc-600);
  margin-bottom: 6px;
}

/* ============================================
   RECOMMENDATION SECTION
   ============================================ */

.personal-recommendation {
  margin-top: 16px;
  padding: 16px;
  background: var(--zinc-50);
  border: 1px solid var(--zinc-200);
}

.key-insight {
  font-size: 12pt;
  font-weight: 500;
  color: var(--zinc-900);
  margin-top: 12px;
  padding-top: 12px;
  border-top: 1px solid var(--zinc-200);
}

/* ============================================
   HYPOTHESIS
   ============================================ */

.hypothesis-list {
  margin-top: 12px;
}

.hypothesis-item {
  padding: 12px;
  margin-bottom: 8px;
  background: var(--zinc-50);
  border: 1px solid var(--zinc-200);
  page-break-inside: avoid;
}

.hypothesis-header {
  display: flex;
  justify-content: space-between;
  align-items: center;
  margin-bottom: 8px;
}

.hypothesis-name {
  font-size: 11pt;
  font-weight: 600;
  color: var(--zinc-900);
}

/* ============================================
   EQUATIONS
   ============================================ */

.equation-box {
  margin-top: 12px;
  padding: 12px;
  background: var(--zinc-100);
  border: 1px solid var(--zinc-200);
}

.equation-box code {
  font-family: 'SF Mono', Monaco, Consolas, monospace;
  font-size: 10pt;
  color: var(--zinc-800);
}

.equation-explanation {
  font-size: 9pt;
  color: var(--zinc-600);
  margin-top: 8px;
}

.physics-box {
  margin-top: 12px;
  padding: 12px;
  background: var(--zinc-100);
  border: 1px solid var(--zinc-200);
}

.physics-box .label {
  font-size: 8pt;
  font-weight: 500;
  color: var(--zinc-500);
  display: block;
  margin-bottom: 4px;
}

.physics-box code {
  font-family: 'SF Mono', Monaco, Consolas, monospace;
  font-size: 10pt;
  color: var(--zinc-800);
}

/* ============================================
   INNOVATION ANALYSIS
   ============================================ */

.section-subtitle {
  font-size: 10pt;
  color: var(--zinc-500);
  margin-top: -12px;
  margin-bottom: 16px;
}

.border-left {
  border-left: 3px solid var(--zinc-900);
  padding-left: 16px;
}

.domains-list {
  display: flex;
  flex-wrap: wrap;
  gap: 8px;
  margin-top: 8px;
}

.domain-tag {
  display: inline-block;
  padding: 4px 12px;
  font-size: 9pt;
  font-weight: 500;
  color: var(--zinc-700);
  background: var(--zinc-50);
  border: 1px solid var(--zinc-200);
  border-radius: 12px;
}

/* ============================================
   FRONTIER TECHNOLOGIES
   ============================================ */

.frontier-grid {
  display: grid;
  grid-template-columns: 1fr 1fr;
  gap: 16px;
  margin-top: 16px;
}

.frontier-card {
  padding: 16px;
  border: 1px solid var(--zinc-200);
  page-break-inside: avoid;
}

.frontier-header {
  display: flex;
  justify-content: space-between;
  align-items: flex-start;
  gap: 12px;
  margin-bottom: 8px;
}

.frontier-title {
  font-size: 12pt;
  font-weight: 600;
  color: var(--zinc-900);
  margin: 0;
}

.frontier-type {
  display: block;
  margin-top: 4px;
  font-size: 8pt;
  font-weight: 500;
  letter-spacing: 0.04em;
  text-transform: uppercase;
  color: var(--zinc-400);
}

.frontier-trl {
  text-align: right;
  flex-shrink: 0;
}

.trl-label {
  font-size: 8pt;
  color: var(--zinc-400);
}

.trl-value {
  font-size: 14pt;
  font-weight: 600;
  color: var(--zinc-700);
}

.frontier-oneliner {
  font-size: 10pt;
  line-height: 1.4;
  color: var(--zinc-600);
  margin-bottom: 12px;
}

.frontier-detail {
  margin-top: 8px;
}

.frontier-meta {
  margin-top: 12px;
  padding-top: 8px;
  border-top: 1px solid var(--zinc-100);
  font-size: 9pt;
  color: var(--zinc-500);
}

.frontier-meta p {
  margin-bottom: 4px;
}

.frontier-meta strong {
  font-weight: 500;
  color: var(--zinc-600);
}

/* ============================================
   PRINT-SPECIFIC RULES
   ============================================ */

@media print {
  body {
    background: white;
  }

  .section {
    page-break-inside: avoid;
  }

  .section-title,
  .subsection-title {
    page-break-after: avoid;
  }

  h1, h2, h3, h4 {
    page-break-after: avoid;
  }

  p {
    orphans: 3;
    widows: 3;
  }

  .page-break {
    page-break-before: always;
  }

  .no-break {
    page-break-inside: avoid;
  }
}
`;
